import React, { useState } from 'react';
import { Routes, Route, useNavigate, Navigate } from 'react-router-dom';
import DashboardPage from './components/customer/DashboardPage';
import RegisterPage from './components/customer/RegisterPage';
import LoginPage from './components/customer/LoginPage';
import EmployeeLoginPage from './components/employee/EmployeeLoginPage';
import EmployeeDashboardPage from './components/employee/EmployeeDashboardPage'; 
import ProtectedRoute from './components/ProtectedRoute'; 
import { AuthProvider, useAuth } from './AuthContext.js'; // Make sure useAuth is imported

function App() {
  const navigate = useNavigate();
  const { user } = useAuth(); // Assuming you have user data in context
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData), 
      });

      if (response.ok) {
        const data = await response.json();
        localStorage.setItem('token', data.token);
        navigate('/dashboard'); 
      } else {
        const error = await response.json();
        setError(error.message || 'Login failed');
      }
    } catch (error) {
      console.error('Error:', error);
      setError('An error occurred. Please try again later.');
    }
  };

  return (
    <div className="App">
      <AuthProvider>
        <Routes>
          {/* Public Routes */}
          <Route path="/" element={<Navigate to={user ? "/dashboard" : "/login"} />} /> {/* Redirect based on authentication */}
          <Route path="/register" element={user ? <Navigate to="/dashboard" /> : <RegisterPage />} /> {/* Redirect to dashboard if already logged in */}
          <Route path="/login" element={user ? <Navigate to="/dashboard" /> : <LoginPage />} /> {/* Redirect to dashboard if already logged in */}

          {/* Protected Routes */}
          <Route 
            path="/dashboard" 
            element={
              <ProtectedRoute>
                <DashboardPage />
              </ProtectedRoute>
            } 
          />

          <Route 
            path="/employee-dashboard" 
            element={
              <ProtectedRoute>
                <EmployeeDashboardPage />
              </ProtectedRoute>
            } 
          />

          {/* Employee Routes */}
          <Route path="/employee" element={<EmployeeLoginPage />} /> {/* Employee login route */}
        </Routes>
      </AuthProvider>
    </div>
  );
}

export default App;
