import React, { useState, useEffect } from 'react';
import './../customer/css/DashboardPage.css';
import Notification from './../../Notification';
import Navbar from '../../components/Navbar';
import { useAuth } from '../../../src/AuthContext';

function DashboardPage() {
  const { user } = useAuth();

  const [transactions, setTransactions] = useState([]);
  const [newTransaction, setNewTransaction] = useState({
    amount: '',
    currency: '',
    provider: 'SWIFT',
    accountNumber: '',
    swiftCode: '',
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const accountNumber = user?.accountNumber;

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const response = await fetch(`https://localhost:7150/api/Transactions/customer/${user.accountNumber}`);
        if (!response.ok) throw new Error('Failed to fetch transactions');
        const data = await response.json();
        setTransactions(data);
      } catch (err) {
        console.error(err);
        setError('Could not load transactions.');
      }
    };

    if (user?.id) {
      fetchTransactions();
    }
  }, [user]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewTransaction(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Prepare the payload with transaction details and user account number
    const payload = {
      ...newTransaction,  // This includes amount, currency, etc.
      accountNumber: user.accountNumber,  // Make sure the user has accountNumber in their data
    };

    try {
      // Send POST request to create a new transaction
      const response = await fetch('https://localhost:7150/api/transactions', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });

      // Handle if the response is not successful
      if (!response.ok) throw new Error('Transaction failed');

      // Parse the saved transaction data from the response
      const savedTransaction = await response.json();

      // Update the transactions list with the newly created transaction
      setTransactions([savedTransaction, ...transactions]);

      // Provide feedback to the user
      setSuccess('Transaction submitted successfully');

      // Reset the transaction form
      setNewTransaction({
        amount: '',
        currency: '',
        provider: 'SWIFT',  // Default provider, change as necessary
        accountNumber: '',  // Clear account number (may be prefilled from `user` if needed)
        swiftCode: '',
      });

      // Close the modal (if using one)
      setIsModalOpen(false);
    } catch (err) {
      console.error(err);
      setError('Failed to submit transaction');
    }
  };


  const toggleModal = () => setIsModalOpen(!isModalOpen);

  return (
    <>
      <Navbar userType="Customer" />
      <div className="dashboard-container">
        <h2>Customer Dashboard</h2>
        <h3>Welcome, {user?.fullName}</h3>

        {success && <Notification message={success} type="success" />}
        {error && <Notification message={error} type="error" />}

        <h3>Transaction History</h3>
        <button onClick={toggleModal}>Add New Transaction</button>

        <table>
          <thead>
            <tr>
              <th>Amount</th>
              <th>Currency</th>
              <th>Provider</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map(transaction => (
              <tr key={transaction.id}>
                <td>{transaction.amount}</td>
                <td>{transaction.currency}</td>
                <td>{transaction.provider}</td>
                <td>{transaction.status}</td>
              </tr>
            ))}
          </tbody>
        </table>

        {isModalOpen && (
  <div className="modal">
    <div className="modal-content">
      <h3>Initiate a New Payment</h3>
      <h4>Welcome, {user?.id}</h4>
      <form onSubmit={handleSubmit}>
  <input
    type="text"
    value={newTransaction.amount}
    onChange={(e) => setNewTransaction({ ...newTransaction, amount: e.target.value })}
    placeholder="Amount"
  />
  <input
    type="text"
    value={newTransaction.currency}
    onChange={(e) => setNewTransaction({ ...newTransaction, currency: e.target.value })}
    placeholder="Currency"
  />
  <input
    type="text"
    value={newTransaction.swiftCode}
    onChange={(e) => setNewTransaction({ ...newTransaction, swiftCode: e.target.value })}
    placeholder="SWIFT Code"
  />
  <button type="submit">Submit Transaction</button>
</form>

      <button onClick={toggleModal}>Close</button>
    </div>
  </div>
)}
      </div>
    </>
  );
}

export default DashboardPage;
