import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './../customer/css/LoginPage.css'; // Styling file
import { useAuth } from  '../../../src/AuthContext';

function LoginPage() {
  const navigate = useNavigate();
  const { setUser } = useAuth(); // ðŸ‘ˆ Pull in context to store user info
  const [formData, setFormData] = useState({
    fullName: '',
    accountNumber: '',
    password: '',
  });
  const { login } = useAuth();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const handleSubmit = async (e) => {
        e.preventDefault();
        try {
          const response = await fetch('https://localhost:7150/api/Customers/Login', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
              fullName: formData.fullName,
              accountNumber: formData.accountNumber,
              PasswordHash: formData.password, // Change 'password' to 'PasswordHash'
            }),
          });

          if (response.ok) {
            const data = await response.json();
            console.log('Login response:', data);

            localStorage.setItem('token', data.token);
            setUser({
              fullName: data.customer.fullName,
              accountNumber: data.customer.accountNumber,
              id: data.customer.id,
            });

            navigate('/dashboard');
          } else {
            const error = await response.json();
            alert(error.message || 'Login failed');
          }
        } catch (err) {
          console.error('Login Error:', err);
          alert('Something went wrong. Please try again.');
        }
      };


      if (response.ok) {
        const data = await response.json();
        console.log('Login response:', data);

        localStorage.setItem('token', data.token); // Store token if needed
        setUser({ // ðŸ‘ˆ Save user info in global context
          fullName: data.fullName,
          accountNumber: data.accountNumber,
          id: data.id,
        });

        navigate('/dashboard'); // Navigate to dashboard after login
      } else {
        const error = await response.json();
        alert(error.message || 'Login failed');
      }
    } catch (err) {
      console.error('Login Error:', err);
      alert('Something went wrong. Please try again.');
    }
  };

  return (
    <div className="login-container">
      <h2>Customer Login</h2>
      <form onSubmit={handleSubmit} className="login-form">
        <div className="form-group">
          <label>Full Name</label>
          <input
            type="text"
            name="fullName"
            value={formData.fullName}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Account Number</label>
          <input
            type="text"
            name="accountNumber"
            value={formData.accountNumber}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Password</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="login-btn">Login</button>
      </form>
    </div>
  );
}

export default LoginPage;
