import React, { useState } from 'react';
import { useAuth } from '../../../src/AuthContext'; // Assuming you have authentication context

function DashboardPage() {
  const { user } = useAuth(); // Get the current user (customer)
  const [newTransaction, setNewTransaction] = useState({
    amount: '',
    currency: 'USD',  // Default value
    provider: 'SWIFT', // Default value
    swiftCode: '',
  });

  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Handle input changes for the form
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewTransaction((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();  // Prevent default form submission

    // Prepare transaction data, including the entire object in the "transaction" field
    const transactionData = {
      amount: parseFloat(newTransaction.amount),
      currency: newTransaction.currency,
      provider: newTransaction.provider,
      status: "Pending",
      customerId: user.id,  // or whatever your logged-in user ID is
    };

    fetch("https://localhost:7150/api/transactions", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(transactionData),
    })
      .then(async (response) => {
        const contentType = response.headers.get("content-type");

        if (response.ok) {
          const data = await response.json();
          setSuccess("Transaction submitted successfully.");
        } else {
          let errorMessage = "Something went wrong.";
          if (contentType && contentType.includes("application/json")) {
            const errorData = await response.json();
            errorMessage = JSON.stringify(errorData, null, 2);
          } else {
            const errorText = await response.text();
            errorMessage = errorText;
          }
          setError(errorMessage);
        }
      })
      .catch((error) => {
        setError("Network or server error: " + error.message);
      });

  };

  return (
    <div>
      <h2>Submit Transaction</h2>

      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>{success}</p>}

      <form onSubmit={handleSubmit}>
        <div>
          <label>Amount</label>
          <input
            type="number"
            name="amount"
            value={newTransaction.amount}
            onChange={handleInputChange}
            placeholder="Enter amount"
            required
          />
        </div>

        <div>
          <label>Currency</label>
          <select
            name="currency"
            value={newTransaction.currency}
            onChange={handleInputChange}
            required
          >
            <option value="USD">USD</option>
            <option value="EUR">EUR</option>
            <option value="GBP">GBP</option>
            <option value="ZAR">ZAR</option>
          </select>
        </div>

        <div>
          <label>SWIFT Code</label>
          <input
            type="text"
            name="swiftCode"
            value={newTransaction.swiftCode}
            onChange={handleInputChange}
            placeholder="Enter SWIFT Code"
            required
          />
        </div>

        <button type="submit">Submit Transaction</button>
      </form>
    </div>
  );
}

export default DashboardPage;
