import React, { useState } from 'react';
import { Routes, Route, useNavigate, Navigate } from 'react-router-dom'; // Use Navigate for redirection
import DashboardPage from './DashboardPage';

function App() {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        const data = await response.json();
        localStorage.setItem('token', data.token); // Store the token
        navigate('/dashboard'); // Redirect to dashboard
      } else {
        const error = await response.json();
        alert(error.message || 'Login failed');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred. Please try again later.');
    }
  };

  return (
    <div className="App">
      <Routes>
        {/* Default route redirects to login page */}
        <Route 
          path="/" 
          element={<Navigate to="/login" />} // Redirect to login page
        />

        {/* Login route */}
        <Route 
          path="/login" 
          element={(
            <>
              <h2>Login</h2>
              <form onSubmit={handleSubmit}>
                <div>
                  <label htmlFor="username">Username</label>
                  <input
                    type="text"
                    id="username"
                    name="username"
                    value={formData.username}
                    onChange={handleInputChange}
                    required
                  />
                </div>
                <div>
                  <label htmlFor="password">Password</label>
                  <input
                    type="password"
                    id="password"
                    name="password"
                    value={formData.password}
                    onChange={handleInputChange}
                    required
                  />
                </div>
                <button type="submit">Login</button>
              </form>
            </>
          )}
        />

        {/* Dashboard route */}
        <Route 
          path="/dashboard" 
          element={<DashboardPage />} // Ensure DashboardPage is defined and imported
        />
      </Routes>
    </div>
  );
}

export default App;
