import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from './Navbar'; // make sure this component exists
import Notification from './Notification'; // reusable notification component
import './../customer/css/LoginPage.css'; // Your styling

const apiBaseUrl = 'https://your-api-url/api/TransactionDetails'; // replace with your API URL

const TransDetailPage = ({ user }) => {
  const [transactions, setTransactions] = useState([]);
  const [newTransaction, setNewTransaction] = useState({
    amount: '',
    currency: 'USD',
    provider: 'SWIFT',
    status: 'Pending',
    date: new Date().toISOString()
  });
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    fetchTransactions();
  }, []);

  const fetchTransactions = async () => {
    try {
      const res = await axios.get(apiBaseUrl);
      setTransactions(res.data);
    } catch (err) {
      console.error(err);
      setError('Failed to fetch transactions.');
    }
  };

  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
    setNewTransaction({
      amount: '',
      currency: 'USD',
      provider: 'SWIFT',
      status: 'Pending',
      date: new Date().toISOString()
    });
    setSuccess('');
    setError('');
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewTransaction({ ...newTransaction, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(apiBaseUrl, newTransaction);
      setSuccess('Transaction created successfully!');
      fetchTransactions();
      toggleModal();
    } catch (err) {
      console.error(err);
      setError('Failed to create transaction.');
    }
  };

  return (
    <>
      <Navbar userType="Customer" />
      <div className="dashboard-container">
        <h2>Customer Dashboard</h2>
        <h3>Welcome, {user?.fullName}</h3>

        {success && <Notification message={success} type="success" />}
        {error && <Notification message={error} type="error" />}

        <h3>Transaction History</h3>
        <button onClick={toggleModal}>Add New Transaction</button>

        <table>
          <thead>
            <tr>
              <th>Amount</th>
              <th>Currency</th>
              <th>Provider</th>
              <th>Status</th>
              <th>Date</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map(transaction => (
              <tr key={transaction.id}>
                <td>{transaction.amount}</td>
                <td>{transaction.currency}</td>
                <td>{transaction.provider}</td>
                <td>{transaction.status}</td>
                <td>{new Date(transaction.date).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>

        {isModalOpen && (
          <div className="modal">
            <div className="modal-content">
              <h3>Initiate a New Payment</h3>
              <form onSubmit={handleSubmit}>
                <div>
                  <label>Amount</label>
                  <input
                    type="number"
                    name="amount"
                    value={newTransaction.amount}
                    onChange={handleInputChange}
                    required
                  />
                </div>
                <div>
                  <label>Currency</label>
                  <select
                    name="currency"
                    value={newTransaction.currency}
                    onChange={handleInputChange}
                  >
                    <option value="USD">USD</option>
                    <option value="EUR">EUR</option>
                    <option value="GBP">GBP</option>
                    <option value="ZAR">ZAR</option>
                  </select>
                </div>
                <div>
                  <label>Provider</label>
                  <input
                    type="text"
                    name="provider"
                    value={newTransaction.provider}
                    readOnly
                  />
                </div>
                <div>
                  <label>Status</label>
                  <input
                    type="text"
                    name="status"
                    value={newTransaction.status}
                    readOnly
                  />
                </div>
                <div>
                  <label>Date</label>
                  <input
                    type="datetime-local"
                    name="date"
                    value={new Date(newTransaction.date).toISOString().slice(0, 16)}
                    onChange={handleInputChange}
                  />
                </div>
                <button type="submit">Pay Now</button>
              </form>
              <button onClick={toggleModal}>Close</button>
            </div>
          </div>
        )}
      </div>
    </>
  );
};

export default TransDetailPage;
