import axios from 'axios';

const apiUrl = 'https://yourapiurl/api/transactiondetails';

const getTransactionDetails = async () => {
  const response = await axios.get(apiUrl);
  return response.data;
};

const getTransactionDetailById = async (id) => {
  const response = await axios.get(`${apiUrl}/${id}`);
  return response.data;
};

const createTransactionDetail = async (transaction) => {
  const response = await axios.post(apiUrl, transaction);
  return response.data;
};

const updateTransactionDetail = async (id, transaction) => {
  const response = await axios.put(`${apiUrl}/${id}`, transaction);
  return response.data;
};

const deleteTransactionDetail = async (id) => {
  const response = await axios.delete(`${apiUrl}/${id}`);
  return response.data;
};

export {
  getTransactionDetails,
  getTransactionDetailById,
  createTransactionDetail,
  updateTransactionDetail,
  deleteTransactionDetail
};
