import React, { useEffect, useState } from 'react';
import axios from 'axios';

function PaymentsPage() {
    const [payments, setPayments] = useState([]);
    const [newPayment, setNewPayment] = useState({
        amount: '',
        currency: '',
        status: '',
        paymentDate: '',
        customerId: ''
    });

    // Load payments on page load
    useEffect(() => {
        fetchPayments();
    }, []);

    const fetchPayments = () => {
        axios.get('https://localhost:7150/api/payments')
            .then(response => setPayments(response.data))
            .catch(error => console.error('Error fetching payments:', error));
    };

    const handleChange = (e) => {
        setNewPayment({
            ...newPayment,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post('https://localhost:7150/api/payments', newPayment)
            .then(() => {
                fetchPayments(); // Reload list after adding
                setNewPayment({
                    amount: '',
                    currency: '',
                    status: '',
                    paymentDate: '',
                    customerId: ''
                });
            })
            .catch(error => console.error('Error adding payment:', error));
    };

    return (
        <div>
            <h1>Payments</h1>

            {/* Add Payment Form */}
            <form onSubmit={handleSubmit} style={{ marginBottom: '20px' }}>
                <input
                    type="number"
                    name="amount"
                    placeholder="Amount"
                    value={newPayment.amount}
                    onChange={handleChange}
                    required
                />
                <input
                    type="text"
                    name="currency"
                    placeholder="Currency"
                    value={newPayment.currency}
                    onChange={handleChange}
                    required
                />
                <input
                    type="text"
                    name="status"
                    placeholder="Status"
                    value={newPayment.status}
                    onChange={handleChange}
                    required
                />
                <input
                    type="datetime-local"
                    name="paymentDate"
                    value={newPayment.paymentDate}
                    onChange={handleChange}
                    required
                />
                <input
                    type="number"
                    name="customerId"
                    placeholder="Customer ID"
                    value={newPayment.customerId}
                    onChange={handleChange}
                    required
                />
                <button type="submit">Add Payment</button>
            </form>

            {/* Payments Table */}
            <table border="1" cellPadding="5">
                <thead>
                    <tr>
                        <th>Amount</th>
                        <th>Currency</th>
                        <th>Status</th>
                        <th>Payment Date</th>
                        <th>Customer ID</th>
                    </tr>
                </thead>
                <tbody>
                    {payments.map(payment => (
                        <tr key={payment.id}>
                            <td>{payment.amount}</td>
                            <td>{payment.currency}</td>
                            <td>{payment.status}</td>
                            <td>{new Date(payment.paymentDate).toLocaleString()}</td>
                            <td>{payment.customerId}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default PaymentsPage;
