import React, { useEffect, useState } from 'react';
import { getTransactionDetails, deleteTransactionDetail } from './TransactionDetailsAPI';
import { Link } from 'react-router-dom';

const TransactionList = () => {
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    const fetchTransactions = async () => {
      const data = await getTransactionDetails();
      setTransactions(data);
    };

    fetchTransactions();
  }, []);

  const handleDelete = async (id) => {
    await deleteTransactionDetail(id);
    setTransactions(transactions.filter((transaction) => transaction.id !== id));
  };

  return (
    <div>
      <h2>Transaction Details</h2>
      <table>
        <thead>
          <tr>
            <th>Amount</th>
            <th>Currency</th>
            <th>Provider</th>
            <th>Status</th>
            <th>Date</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {transactions.map((transaction) => (
            <tr key={transaction.id}>
              <td>{transaction.amount}</td>
              <td>{transaction.currency}</td>
              <td>{transaction.provider}</td>
              <td>{transaction.status}</td>
              <td>{new Date(transaction.date).toLocaleString()}</td>
              <td>
                <Link to={`/edit/${transaction.id}`}>Edit</Link>
                <button onClick={() => handleDelete(transaction.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <Link to="/create">Create New Transaction</Link>
    </div>
  );
};

export default TransactionList;
