// EmployeeDashboardPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Navigate } from 'react-router-dom';

const apiBaseUrl = 'https://localhost:7150/api/TransactionDetails'; // Replace with actual backend API

const EmployeeDashboardPage = () => {
  const [transactions, setTransactions] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(true);
  const employee = JSON.parse(localStorage.getItem('employee')); // from login response

  useEffect(() => {
    const token = localStorage.getItem('employeeToken');
    if (!token) {
      setIsAuthenticated(false); // Redirect if not authenticated
    } else {
      fetchTransactions(token); // Fetch transactions if authenticated
    }
  }, []);

  const fetchTransactions = async (token) => {
    try {
      const res = await axios.get(apiBaseUrl, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTransactions(res.data);
    } catch (err) {
      console.error('Error fetching transactions:', err);
    }
  };

  // Redirect if not authenticated
  if (!isAuthenticated) {
    return <Navigate to="/employee-login" />;
  }

  return (
    <div style={{ maxWidth: 800, margin: '0 auto', padding: 20 }}>
      <h2>Welcome {employee?.fullName || 'Admin'}</h2>
      <h2>Employee Dashboard</h2>
      <table border="1" width="100%">
        <thead>
          <tr>
            <th>ID</th>
            <th>Amount</th>
            <th>Currency</th>
            <th>Provider</th>
            <th>Status</th>
            <th>Date</th>
          </tr>
        </thead>
        <tbody>
          {transactions.map((tx) => (
            <tr key={tx.id}>
              <td>{tx.id}</td>
              <td>{tx.amount}</td>
              <td>{tx.currency}</td>
              <td>{tx.provider}</td>
              <td>{tx.status}</td>
              <td>{new Date(tx.date).toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default EmployeeDashboardPage;
