import React, { useEffect, useState } from 'react';
import { useAuth } from '../../AuthContext';
import { useNavigate } from 'react-router-dom';
import './../employee/css/EmployeeDashboardPage.css'; // Your styles
// import Navbar from '../employee/Navbar.js';
import Navbar from './Navbar'; // Make sure the path is correct

function EmployeeDashboardPage() {
  const { user } = useAuth();
  const navigate = useNavigate();

  const [transactions, setTransactions] = useState([]);
  const [selectedTransaction, setSelectedTransaction] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    // Fetch all transactions on page load
    const fetchTransactions = async () => {
      const response = await fetch('https://localhost:7150/api/Transactions');
      const data = await response.json();
      setTransactions(data);
    };
    fetchTransactions();
  }, []);

  const openModal = (transaction) => {
    setSelectedTransaction(transaction);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedTransaction(null);
  };

  const handleVerify = async () => {
    const updatedTransaction = { ...selectedTransaction, status: 'Verified' };

    // Call the API to update the status of the transaction
    const response = await fetch(`https://localhost:7150/api/Transactions/${selectedTransaction.id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(updatedTransaction),
    });

    if (response.ok) {
      setTransactions((prev) => prev.map((txn) => txn.id === selectedTransaction.id ? updatedTransaction : txn));
      closeModal();
    } else {
      alert('Failed to verify transaction');
    }
  };

  return (
    <div className="dashboard-container">
      <h2>Employee Dashboard</h2>
      <table className="transactions-table">
        <thead>
          <tr>
            <th>Transaction ID</th>
            <th>Account Number</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {transactions.map((transaction) => (
            <tr key={transaction.id}>
              <td>{transaction.id}</td>
              <td>{transaction.accountNumber}</td>
              <td>{transaction.status}</td>
              <td>
                <button onClick={() => openModal(transaction)}>Verify</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {isModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <h3>Verify Transaction</h3>
            <p>Are you sure you want to verify this transaction?</p>
            <p><strong>Transaction ID:</strong> {selectedTransaction.id}</p>
            <p><strong>Account Number:</strong> {selectedTransaction.accountNumber}</p>
            <div className="modal-buttons">
              <button onClick={handleVerify}>Verify</button>
              <button onClick={closeModal}>Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default EmployeeDashboardPage;
