import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../../src/AuthContext';
import './../customer/css/LoginPage.css'; // Your styling

function LoginPage() {
  const navigate = useNavigate();
  const { setUser } = useAuth();
  const [formData, setFormData] = useState({
    fullName: '',
    accountNumber: '',
    password: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };
  const [errors, setErrors] = useState({});
  const [error, setError] = useState('');
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('https://localhost:7150/api/Customers/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          fullName: formData.fullName,
          accountNumber: formData.accountNumber,
          password: formData.password
        }),
      });

      const data = await response.json();

      if (response.ok) {
        setError('');
        setUser(data.customer); // Save user globally

        // Redirect based on role
        if (data.customer.role === "customer") {
          navigate('/dashboard');
        } else if (data.customer.role === "employee") {
          navigate('/employee-dashboard');
        } else {
          navigate('/'); // fallback route
        }

      } else {
        setError(data.message || 'Login failed');
      }

    } catch (err) {
      setError('Login failed. Please try again.');
    }
  };



  return (
    <div className="login-container">
      <h2>Customer Login</h2>
      <form onSubmit={handleSubmit} className="login-form">
        <div className="form-group">
          <label>Full Name</label>
          <input
            type="text"
            name="fullName"
            value={formData.fullName}
            onChange={handleChange}

          />
        </div>
        <div className="form-group">
          <label>Account Number</label>
          <input
            type="text"
            name="accountNumber"
            value={formData.accountNumber}
            onChange={handleChange}

          />
        </div>
        <div className="form-group">
          <label>Password</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}

          />
                    {errors.password && <div className="error-message">{errors.password}</div>}

        </div>
        <button type="submit" className="login-btn">Login</button>
        {error && (
    <div style={{ color: 'red', marginTop: '10px' }}>
      {error}
    </div>
  )}

      </form>
    </div>
  );
}

export default LoginPage;
