import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';  // If you want to handle routes with params
import { mockTransactions } from './../../mockData'; // For initial mock data
import './../employee/css/EmployeeDashboardPage.css'; // Add custom styles
import Notification from './../../Notification'; // Import the Notification component
import Navbar from '../../components/Navbar';

function EmployeeDashboardPage() {
  const [transactions, setTransactions] = useState(mockTransactions); // Use mock data initially
  const [selectedTransaction, setSelectedTransaction] = useState(null); // Store selected transaction for modal
  const [selectedCustomer, setSelectedCustomer] = useState(null); // Store customer info
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false); // Track if the modal is open
  const [status, setStatus] = useState(''); // For dropdown

  // Function to fetch customer info based on the transaction's CustomerId
  const fetchCustomerInfo = async (customerId) => {
    try {
      const response = await fetch(`https://localhost:7150/api/Employee/customer-from-transaction/${customerId}`);
      if (!response.ok) {
        throw new Error('Error fetching customer data');
      }
      const data = await response.json();
      setSelectedCustomer(data); // Set the customer data for modal
    } catch (err) {
      setError('Failed to fetch customer info');
    }
  };

  // Open the modal to verify or flag a transaction
  const openModal = async (transaction) => {
    setSelectedTransaction(transaction);
    setIsModalOpen(true);
    setStatus(transaction.status); // Set the current status for the dropdown
    await fetchCustomerInfo(transaction.customerId); // Fetch customer info for the selected transaction
  };

  // Close the modal
  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedTransaction(null);
    setSelectedCustomer(null); // Clear customer info when closing the modal
  };

  // Handle save action
  const handleSave = async () => {
    // Perform the necessary API call to update status
    try {
      const response = await fetch(`https://localhost:7150/api/Employee/verify/${selectedTransaction.id}`, {
        method: 'PUT',
        body: JSON.stringify({
          status: status,
        }),
      });
      if (response.ok) {
        setSuccess('Transaction status updated successfully');
        setTransactions(prevTransactions =>
          prevTransactions.map(tx =>
            tx.id === selectedTransaction.id ? { ...tx, status } : tx
          )
        );
        closeModal(); // Close modal after action
      } else {
        throw new Error('Failed to update status');
      }
    } catch (err) {
      setError('Failed to update transaction status');
    }
  };

  return (
    <>
      <Navbar userType="Employee" />
      <div className="employee-dashboard-container">
        <h2>Employee Dashboard</h2>

        {success && <Notification message={success} type="success" />}
        {error && <Notification message={error} type="error" />}

        <h3>Pending Transactions</h3>
        <table>
          <thead>
            <tr>
              <th>Amount</th>
              <th>Currency</th>
              <th>Provider</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map((transaction) => (
              <tr key={transaction.id}>
                <td>{transaction.amount}</td>
                <td>{transaction.currency}</td>
                <td>{transaction.provider}</td>
                <td>{transaction.status}</td>
                <td>
                  <button
                    disabled={transaction.status === 'verified' || transaction.status === 'flagged'}
                    onClick={() => openModal(transaction)}
                  >
                    Open
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {/* Modal for verifying or flagging a transaction */}
        {isModalOpen && selectedTransaction && selectedCustomer && (
          <div className="modal-overlay">
            <div className="modal-content">
              <h3>Transaction Details</h3>
              <p><strong>Amount:</strong> {selectedTransaction.amount}</p>
              <p><strong>Currency:</strong> {selectedTransaction.currency}</p>
              <p><strong>Provider:</strong> {selectedTransaction.provider}</p>
              <p><strong>Status:</strong> {selectedTransaction.status}</p>

              <h4>Customer Information</h4>
              <p><strong>Account Number:</strong> {selectedCustomer.accountNumber}</p>
              <p><strong>Full Name:</strong> {selectedCustomer.fullName}</p>

              <label>Status</label>
              <select
                value={status}
                onChange={(e) => setStatus(e.target.value)} // Update status based on dropdown selection
              >
                <option value="Pending">Pending</option>
                <option value="Verified">Verified</option>
                <option value="Declined">Declined</option>
              </select>

              <button onClick={handleSave}>Save</button>
              <button onClick={closeModal}>Close</button>
            </div>
          </div>
        )}
      </div>
    </>
  );
}

export default EmployeeDashboardPage;
