import React, { useState, useEffect } from 'react';
import './../customer/css/DashboardPage.css';
import Notification from './../../Notification';
import Navbar from '../../components/Navbar';
import { useLocation } from 'react-router-dom';

function DashboardPage() {
  const [transactions, setTransactions] = useState([]);
  const [newTransaction, setNewTransaction] = useState({
    amount: '',
    currency: '',
    provider: 'SWIFT',
    accountNumber: '',
    swiftCode: '',
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);

  // ðŸ‘‡ You can adjust this depending on how you're storing login info
  const accountNumber = localStorage.getItem('accountNumber');

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const response = await fetch(`https://localhost:7150/customer/${accountNumber}`);
        if (!response.ok) throw new Error('Failed to fetch transactions');
        const data = await response.json();
        setTransactions(data);
      } catch (err) {
        console.error(err);
        setError('Could not load transactions.');
      }
    };

    if (accountNumber) {
      fetchTransactions();
    }
  }, [accountNumber]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewTransaction(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('https://localhost:7150/api/customers/transactions', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newTransaction),
      });

      if (!response.ok) throw new Error('Transaction failed');
      const savedTransaction = await response.json();
      setTransactions([savedTransaction, ...transactions]);
      setSuccess('Transaction submitted successfully');
      setNewTransaction({
        amount: '',
        currency: '',
        provider: 'SWIFT',
        accountNumber:accountNumber,
        swiftCode: '',
      });
      setIsModalOpen(false);
    } catch (err) {
      console.error(err);
      setError('Failed to submit transaction');
    }
  };

  const toggleModal = () => setIsModalOpen(!isModalOpen);

  return (
    <>
      <Navbar userType="Customer" />
      <div className="dashboard-container">
        <h2>Customer Dashboard</h2>

        {success && <Notification message={success} type="success" />}
        {error && <Notification message={error} type="error" />}

        <h3>Transaction History</h3>
        <table>
          <thead>
            <tr>
              <th>Amount</th>
              <th>Currency</th>
              <th>Provider</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map(transaction => (
              <tr key={transaction.id}>
                <td>{transaction.amount}</td>
                <td>{transaction.currency}</td>
                <td>{transaction.provider}</td>
                <td>{transaction.status}</td>
              </tr>
            ))}
          </tbody>
        </table>

        <button onClick={toggleModal}>Add New Transaction</button>

        {isModalOpen && (
          <div className="modal">
            <div className="modal-content">
              <h3>Initiate a New Payment</h3>
              <form onSubmit={handleSubmit}>
                <div>
                  <label>Amount</label>
                  <input type="number" name="amount" value={newTransaction.amount} onChange={handleInputChange} required />
                </div>
                <div>
                  <label>Currency</label>
                  <input type="text" name="currency" value={newTransaction.currency} onChange={handleInputChange} required />
                </div>
                <div>
                  <label>Provider</label>
                  <select name="provider" value={newTransaction.provider} onChange={handleInputChange} required>
                    <option value="SWIFT">SWIFT</option>
                  </select>
                </div>
                <div>
                  <label>Account Number</label>
                  <input type="text" name="accountNumber" value={newTransaction.accountNumber} onChange={handleInputChange} required />
                </div>
                <div>
                  <label>SWIFT Code</label>
                  <input type="text" name="swiftCode" value={newTransaction.swiftCode} onChange={handleInputChange} required />
                </div>
                <button type="submit">Pay Now</button>
              </form>
              <button onClick={toggleModal}>Close</button>
            </div>
          </div>
        )}
      </div>
    </>
  );
}

export default DashboardPage;
