import React, { useState } from 'react';
import { useAuth } from '../../../src/AuthContext'; // Assuming you have authentication context

function DashboardPage() {
  const { user } = useAuth(); // Get the current user
  const [newTransaction, setNewTransaction] = useState({
    amount: '',
    currency: '',
    provider: 'SWIFT',
    swiftCode: '',
  });

  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Handle input changes for the form
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewTransaction((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Handle form submission
// Prepare transaction data
const transactionData = {
  amount: 100.00,
  currency: "USD",
  provider: "BankName",
  status: "Pending",
  customerId: 3  // Only include the customerId here
};

// Send the transaction data to the backend API
fetch("https://localhost:7150/api/transactions", {
  method: "POST",  // Specify the HTTP method (POST)
  headers: {
    "Content-Type": "application/json",  // Set content type to JSON
  },
  body: JSON.stringify(transactionData),  // Convert the JavaScript object to a JSON string
})
  .then(response => response.json())  // Parse the JSON response
  .then(data => {
    if (response.ok) {
      console.log("Transaction created successfully:", data);
    } else {
      console.error("Error creating transaction:", data);
    }
  })
  .catch(error => {
    console.error("Request failed", error);
  });

  return (
    <div>
      <h2>Submit Transaction</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>{success}</p>}

      <form onSubmit={handleSubmit}>
        <div>
          <label>Amount</label>
          <input
            type="number"
            name="amount"
            value={newTransaction.amount}
            onChange={handleInputChange}
            placeholder="Enter amount"
            required
          />
        </div>

        <div>
          <label>Currency</label>
          <select
            name="currency"
            value={newTransaction.currency}
            onChange={handleInputChange}
            required
          >
            <option value="USD">USD</option>
            <option value="EUR">EUR</option>
            <option value="GBP">GBP</option>
            <option value="ZAR">ZAR</option>
          </select>
        </div>

        <div>
          <label>SWIFT Code</label>
          <input
            type="text"
            name="swiftCode"
            value={newTransaction.swiftCode}
            onChange={handleInputChange}
            placeholder="Enter SWIFT Code"
            required
          />
        </div>

        <button type="submit">Submit Transaction</button>
      </form>
    </div>
  );
}

export default DashboardPage;
