import React, { useState, useEffect } from 'react';
import Notification from './../../Notification';
import Navbar from '../../components/Navbar';
import { useAuth } from '../../AuthContext';
import '.employee/css/EmployeeDashboard.css';
import './../customer/css/DashboardPage.css';

function EmployeeDashboard() {
  const { user } = useAuth();
  const [transactions, setTransactions] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedTransaction, setSelectedTransaction] = useState(null);

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const response = await fetch('https://localhost:7150/api/transactions');
        if (!response.ok) throw new Error('Failed to fetch transactions');
        const data = await response.json();
        setTransactions(data);
      } catch (err) {
        console.error(err);
        setError('Could not load transactions.');
      }
    };

    fetchTransactions();
  }, []);

  const openModal = (transaction) => {
    setSelectedTransaction(transaction);
    setIsModalOpen(true);
    setSuccess('');
    setError('');
  };

  const closeModal = () => {
    setSelectedTransaction(null);
    setIsModalOpen(false);
  };

  const handleVerify = async () => {
    try {
      const response = await fetch(`https://localhost:7150/api/transactions/verify/${selectedTransaction.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          verifiedBy: user.fullName,
        }),
      });

      if (!response.ok) throw new Error('Verification failed');

      const updated = await response.json();

      setTransactions(prev =>
        prev.map(t => (t.id === updated.id ? updated : t))
      );
      setSuccess('Transaction verified successfully.');
      closeModal();
    } catch (err) {
      console.error(err);
      setError('Verification failed.');
    }
  };

  return (
    <>
      <Navbar userType="Employee" />
      <div className="dashboard-container">
        <h2>Employee Dashboard</h2>
        <h3>Welcome, {user?.fullName}</h3>

        {success && <Notification message={success} type="success" />}
        {error && <Notification message={error} type="error" />}

        <h3>All Transactions</h3>
        <table>
          <thead>
            <tr>
              <th>Amount</th>
              <th>Currency</th>
              <th>Provider</th>
              <th>Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map(transaction => (
              <tr key={transaction.id}>
                <td>{transaction.amount}</td>
                <td>{transaction.currency}</td>
                <td>{transaction.provider}</td>
                <td>{transaction.status}</td>
                <td>
                  <button onClick={() => openModal(transaction)}>
                    {transaction.status === 'Verified' ? 'View' : 'Verify'}
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {isModalOpen && selectedTransaction && (
          <div className="modal">
            <div className="modal-content">
              <h3>Transaction Details</h3>
              <p><strong>Amount:</strong> {selectedTransaction.amount}</p>
              <p><strong>Currency:</strong> {selectedTransaction.currency}</p>
              <p><strong>Status:</strong> {selectedTransaction.status}</p>
              <p><strong>Provider:</strong> {selectedTransaction.provider}</p>
              {selectedTransaction.status === 'Verified' ? (
                <p>âœ… Already verified by <strong>{selectedTransaction.verifiedBy}</strong></p>
              ) : (
                <button onClick={handleVerify}>Verify Now</button>
              )}
              <button onClick={closeModal}>Close</button>
            </div>
          </div>
        )}
      </div>
    </>
  );
}

export default EmployeeDashboard;
