import React, { useState, useEffect } from 'react';

const EmployeeDashboardPage = () => {
  const [transactions, setTransactions] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(true);
  const [selectedTransaction, setSelectedTransaction] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [isVerified, setIsVerified] = useState(false);  // Track the verification state
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleVerify = async () => {
    if (selectedTransaction.status === 'Verified') {
      setError('This transaction has already been verified.');
      return;
    }

    try {
      const response = await fetch(`https://localhost:7150/api/TransactionDetails/verify/${selectedTransaction.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
      });

      if (!response.ok) throw new Error('Verification failed');

      const updatedTransaction = await response.json();
      setTransactions(prev => prev.map(t => (t.id === updatedTransaction.id ? updatedTransaction : t)));
      setSuccess('Transaction verified successfully!');
      setIsVerified(true);  // Show the "Submit to SWIFT" button and disable it once submitted
    } catch (err) {
      console.error(err);
      setError('Verification failed. Please try again.');
    }
  };

  const handleSubmitToSWIFT = () => {
    // Simply close the modal once submitted to SWIFT
    setShowModal(false);
    setIsVerified(false);  // Reset verification state for the next transaction
  };

  const openModal = (transaction) => {
    setSelectedTransaction(transaction);
    setShowModal(true);
    setIsVerified(false);  // Reset state when opening a new transaction
  };

  return (
    <div>
      {/* Your Navbar Component Here */}
      <div className="dashboard-container">
        <h2>Employee Dashboard</h2>
        {success && <div style={successMessageStyle}>{success}</div>}
        {error && <div style={errorMessageStyle}>{error}</div>}

        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Amount</th>
              <th>Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map((tx) => (
              <tr key={tx.id}>
                <td>{tx.id}</td>
                <td>{tx.amount}</td>
                <td>{tx.status}</td>
                <td>
                  <button onClick={() => openModal(tx)} disabled={tx.status === 'Verified'}>
                    {tx.status === 'Verified' ? 'Verified' : 'Verify'}
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {showModal && selectedTransaction && (
          <div style={modalOverlayStyle}>
            <div style={modalContentStyle}>
              <h3>Transaction Verification</h3>

              {/* Show only the relevant button depending on the state */}
              {isVerified ? (
                <>
                  <p>âœ… Transaction Verified</p>
                  <button onClick={handleSubmitToSWIFT} disabled>Submit to SWIFT</button> {/* Disabled button */}
                </>
              ) : (
                <>
                  <button onClick={handleVerify}>Confirm Verification</button>
                  <button onClick={handleSubmitToSWIFT} disabled>Submit to SWIFT</button> {/* Disabled initially */}
                </>
              )}
              <br />
              <button onClick={() => setShowModal(false)} style={{ marginTop: '10px' }}>Close</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

// Styling for success/error messages
const successMessageStyle = {
  backgroundColor: 'green',
  color: 'white',
  padding: '10px',
  marginBottom: '20px',
  borderRadius: '5px',
  textAlign: 'center',
};

const errorMessageStyle = {
  backgroundColor: 'red',
  color: 'white',
  padding: '10px',
  marginBottom: '20px',
  borderRadius: '5px',
  textAlign: 'center',
};

// Minimal inline modal styling
const modalOverlayStyle = {
  position: 'fixed', top: 0, left: 0, width: '100%', height: '100%',
  backgroundColor: 'rgba(0, 0, 0, 0.5)', display: 'flex',
  alignItems: 'center', justifyContent: 'center', zIndex: 999,
};

const modalContentStyle = {
  backgroundColor: '#fff', padding: 20, borderRadius: 8,
  width: '400px', maxWidth: '90%',
};

export default EmployeeDashboardPage;
