import React, { useEffect, useState } from 'react';

function EmployeeDashboard() {
    const [transactions, setTransactions] = useState([]);
    const [transaction, setTransaction] = useState(null);
    const [error, setError] = useState('');

    // Fetch all transactions
    const fetchTransactions = async () => {
        const response = await fetch('https://localhost:7150/api/Employee/transactions');
        if (!response.ok) {
            setError('Failed to load transactions.');
            return;
        }
        const data = await response.json();
        setTransactions(data);
    };

    // Fetch a single transaction by ID
    const fetchTransaction = async (id) => {
        const response = await fetch(`https://localhost:7150/api/Employee/transactions/${id}`);
        if (!response.ok) {
            setError('Transaction not found.');
            return;
        }
        const data = await response.json();
        setTransaction(data);
    };

    useEffect(() => {
        fetchTransactions();
    }, []);

    return (
        <div>
            <h2>Employee Dashboard</h2>
            <h3>All Transactions</h3>
            {error && <p>{error}</p>}
            <ul>
                {transactions.map((txn) => (
                    <li key={txn.id}>
                        Transaction ID: {txn.id} - Status: {txn.status}
                        <button onClick={() => fetchTransaction(txn.id)}>View</button>
                    </li>
                ))}
            </ul>

            {transaction && (
                <div>
                    <h3>Transaction Details</h3>
                    <p>ID: {transaction.id}</p>
                    <p>Status: {transaction.status}</p>
                    <p>Amount: {transaction.amount}</p>
                    <p>Account Number: {transaction.accountNumber}</p>
                    <p>Created At: {transaction.createdAt}</p>
                    {/* Additional transaction details */}
                </div>
            )}
        </div>
    );
}

export default EmployeeDashboard;
