import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PaymentsPage = () => {
    // State to store payments and form data
    const [payments, setPayments] = useState([]);
    const [amount, setAmount] = useState('');
    const [currency, setCurrency] = useState('');
    const [customerId, setCustomerId] = useState('');
    const [provider, setProvider] = useState('');

    const API_URL = 'http://localhost:7150/api/payments'; // Replace with your backend URL

    // Fetch all payments when component mounts
    useEffect(() => {
        const fetchPayments = async () => {
            try {
                const response = await axios.get(API_URL);
                setPayments(response.data);
            } catch (error) {
                console.error('Error fetching payments', error);
            }
        };

        fetchPayments();
    }, []);

    // Handle form submission to create payment
    const handleSubmit = async (e) => {
        e.preventDefault();

        const paymentData = {
            amount,
            currency,
            customerId,
            provider,
            date: new Date(),  // Assuming date field is required
        };

        try {
            const response = await axios.post(API_URL, paymentData);
            setPayments([...payments, response.data]);  // Add new payment to list
            // Reset form fields
            setAmount('');
            setCurrency('');
            setCustomerId('');
            setProvider('');
        } catch (error) {
            console.error('Error creating payment', error);
        }
    };

    // Handle deleting a payment
    const handleDelete = async (id) => {
        try {
            await axios.delete(`${API_URL}/${id}`);
            setPayments(payments.filter(payment => payment.id !== id));  // Remove deleted payment from list
        } catch (error) {
            console.error('Error deleting payment', error);
        }
    };

    return (
        <div>
            <h1>Payments</h1>

            {/* Payment Form */}
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Amount:</label>
                    <input
                        type="number"
                        value={amount}
                        onChange={(e) => setAmount(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Currency:</label>
                    <input
                        type="text"
                        value={currency}
                        onChange={(e) => setCurrency(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Customer ID:</label>
                    <input
                        type="text"
                        value={customerId}
                        onChange={(e) => setCustomerId(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Provider:</label>
                    <input
                        type="text"
                        value={provider}
                        onChange={(e) => setProvider(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Add Payment</button>
            </form>

            {/* Display Payments */}
            <h2>Payment List</h2>
            <ul>
                {payments.map((payment) => (
                    <li key={payment.id}>
                        {payment.amount} {payment.currency} - {payment.provider}
                        <button onClick={() => handleDelete(payment.id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default PaymentsPage;
