import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './../employee/css/EmployeeLogin.css'; // Add custom styles

function EmployeeLoginPage() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });
  const [error, setError] = useState('');

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Send POST request to the backend login API
      const response = await fetch('https://localhost:7150/api/employees/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      // Check if the response is successful (status code 2xx)
      if (response.ok) {
        const data = await response.json(); // Parse JSON response

        // Check if token is returned in response
        if (data.token) {
          localStorage.setItem('token', data.token); // Store token in localStorage
          navigate('/employee-dashboard'); // Redirect to employee dashboard
        } else {
          setError('No token returned from server.');
        }
      } else {
        // If the response is not successful, show an error message
        const errorData = await response.json();
        setError(errorData.message || 'Login failed');
      }
    } catch (err) {
      // Catch any network or other errors
      console.error(err);
      setError('Something went wrong. Please try again.');
    }
  };

  return (
    <div className="employee-login-container">
      <h2>Employee Login</h2>
      <form onSubmit={handleSubmit} className="login-form">
        <div className="form-group">
          <label>Username</label>
          <input
            type="text"
            name="username"
            value={formData.username}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Password</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleInputChange}
            required
          />
        </div>
        <button type="submit" className="login-btn">Login</button>
      </form>
      {error && <p style={{ color: 'red' }}>{error}</p>} {/* Display any errors */}
    </div>
  );
}

export default EmployeeLoginPage;
