import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './EmployeeNavbar.css'; // Your styles

const EmployeeNavbar = ({ userType = "Employee" }) => {
  const navigate = useNavigate();

  // Handle logout
  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  // Get the username from localStorage or default to userType
  const name = localStorage.getItem('username') || userType;

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <h3>{name} Dashboard</h3>
      </div>
      <div className="navbar-middle">
        <ul className="navbar-list">
          <li className="navbar-item">
            <Link to="/dashboard" className="navbar-link">Dashboard</Link>
          </li>
          <li className="navbar-item">
            <Link to="/transactions" className="navbar-link">Transactions</Link>
          </li>
        </ul>
      </div>
      <div className="navbar-right">
        <button onClick={handleLogout} className="navbar-logout">Logout</button>
      </div>
    </nav>
  );
};

export default EmployeeNavbar;
