import React, { useState } from 'react';
import { useAuth } from '../../../src/AuthContext'; // Assuming you have authentication context

function DashboardPage() {
  const { user } = useAuth(); // Get the current user
  const [newTransaction, setNewTransaction] = useState({
    amount: '',
    currency: '',
    provider: 'SWIFT',
    swiftCode: '',
  });

  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Handle input changes for the form
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewTransaction((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent the default form submit behavior

    // Validate input
    if (!newTransaction.amount || !newTransaction.currency || !newTransaction.swiftCode) {
      setError('All fields are required');
      return;
    }

    const payload = {
      amount: newTransaction.amount,
      currency: newTransaction.currency,
      provider: "SWIFT",
      swiftCode: newTransaction.swiftCode,
      customerId: "1", // Send the user's ID
    };

    try {
      const response = await fetch('https://localhost:7150/api/transactions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(payload),
      });

      if (!response.ok) {
        throw new Error('Failed to submit transaction');
      }

      const data = await response.json();
      setSuccess('Transaction created successfully!');
      console.log('Response from backend:', data);
    } catch (error) {
      setError(error.message);
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <h2>Submit Transaction</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>{success}</p>}

      <form onSubmit={handleSubmit}>
        <div>
          <label>Amount</label>
          <input
            type="number"
            name="amount"
            value={newTransaction.amount}
            onChange={handleInputChange}
            placeholder="Enter amount"
            required
          />
        </div>

        <div>
          <label>Currency</label>
          <select
            name="currency"
            value={newTransaction.currency}
            onChange={handleInputChange}
            required
          >
            <option value="USD">USD</option>
            <option value="EUR">EUR</option>
            <option value="GBP">GBP</option>
            <option value="ZAR">ZAR</option>
          </select>
        </div>

        <div>
          <label>SWIFT Code</label>
          <input
            type="text"
            name="swiftCode"
            value={newTransaction.swiftCode}
            onChange={handleInputChange}
            placeholder="Enter SWIFT Code"
            required
          />
        </div>

        <button type="submit">Submit Transaction</button>
      </form>
    </div>
  );
}

export default DashboardPage;
