import React, { useState, useEffect } from 'react';
import './../customer/css/DashboardPage.css';
import Notification from './../../Notification';
import Navbar from '../../components/Navbar';
import { useAuth } from '../../../src/AuthContext';

function DashboardPage() {
  const { user } = useAuth();
  console.log('User in dashboard:', user);

  const [transactions, setTransactions] = useState([]);
  const [newTransaction, setNewTransaction] = useState({
    amount: '',
    currency: '',
    accountNumber: '',
    swiftCode: '',
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const accountNumber = user?.accountNumber;

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const response = await fetch(`https://localhost:7150/api/TransactionDetails`);
        if (!response.ok) throw new Error('Failed to fetch transactions');
        const data = await response.json();
        setTransactions(data);
      } catch (err) {
        console.error(err);
        setError('Could not load transactions.');
      }
    };

    if (user?.id) {
      fetchTransactions();
    }
  }, [user]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewTransaction(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const payload = {
      ...newTransaction,
      accountNumber: user.accountNumber,
    };

    try {
      const response = await fetch('https://localhost:7150/api/TransactionDetails', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });

      if (!response.ok) throw new Error('Transaction failed');
      const savedTransaction = await response.json();
      setTransactions([savedTransaction, ...transactions]);
      setSuccess('Transaction submitted successfully');
      setNewTransaction({
        amount: '',
        currency: '',
        accountNumber: '',  
        swiftCode: '',
      });
      setIsModalOpen(false);
    } catch (err) {
      console.error(err);
      setError('Failed to submit transaction');
    }
  };

  const toggleModal = () => setIsModalOpen(!isModalOpen);
  return (
    <>
      <Navbar />
      <div className="dashboard-container">
        <h2>Customer Dashboard</h2>
        {/* <h3>Welcome, {user?.fullName}</h3> */}

        {/* {success && <Notification message={success} type="success" />}
        {error && <Notification message={error} type="error" />} */}

        <h3>Transaction History</h3>
        <button className="verify-btn" onClick={toggleModal}>Add New Transaction</button>

        <table border="1" width="100%">
        <thead>
          <tr>
            <th>ID</th>
            <th>Amount</th>
            <th>Currency</th>
            <th>Swift Code</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {transactions.map((tx) => (
            <tr key={tx.id}>
              <td>{tx.id}</td>
              <td>{tx.amount}</td>
              <td>{tx.currency}</td>
              <td>{tx.swiftCode}</td>
              <td>
                <button className="submitted-badge"onClick={() => handleEdit(tx)}>Edit</button>
                <button  className="verify-btn" onClick={() => handleDelete(tx.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

{/* Modal to add/edit transaction */}
{isModalOpen && (
  <div className="modal">
    <div className="modal-content">
      <h3>{editingId ? 'Edit Transaction' : 'New Transaction'}</h3>

      <form onSubmit={handleSubmit} style={{ marginBottom: 20 }}>
        <div>
          <label>Amount:</label>
          <input type="number" name="amount" value={form.amount} onChange={handleChange} required />
        </div>
        <div>
          <label>Currency:</label>
          <input type="text" name="currency" value={form.currency} onChange={handleChange} required />
        </div>
        <div>
          <label>SWIFT Code:</label>
          <input type="text" name="swiftCode" value={form.swiftCode} onChange={handleChange} required />
        </div>
        <button  type="submit">{editingId ? 'Update' : 'Create'}</button>
        <button onClick={toggleModal} type="button">Close</button>
      </form>
    </div>
  </div>
)}

      </div>
    </>
  );
  return (
    <>
      <Navbar userType="Customer" />
      <div className="dashboard-container">
        <h2>Customer Dashboard</h2>
        <h3>Welcome, {user?.fullName}</h3>

        {success && <Notification message={success} type="success" />}
        {error && <Notification message={error} type="error" />}

        <h3>Transaction History</h3>
        <button onClick={toggleModal}>Add New Transaction</button>

        <table>
          <thead>
            <tr>
              <th>Amount</th>
              <th>Currency</th>
              <th>Swift Code</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map(transaction => (
              <tr key={transaction.id}>
                <td>{transaction.amount}</td>
                <td>{transaction.currency}</td>
                <td>{transaction.swiftCode}</td>
                <td>{transaction.status}</td>
              </tr>
            ))}
          </tbody>
        </table>


      </div>
    </>
  );
}

export default DashboardPage;
